class Solution {
    // Using Fewick Tree data structure to compute the nmber of elements removed before eacg element in num
    // cost of each element = its index - numer of elements that have been removed before itself
    // the number of elements removed before index i can be obtained from the Fenwick Tree
    public String minInteger(String num, int k) {
        int n = num.length();
        List<Queue<Integer>> indices = new ArrayList<>();
        for (int d = 0; d < 10; d++) {
            indices.add(new LinkedList<>());
        }
        for (int i = 0; i < n; i++) {
            int temp = num.charAt(i) - '0';
            indices.get(temp).add(i);
        }
        Fenwick tree = new Fenwick(n + 1);
        StringBuilder res = new StringBuilder();
        boolean[] removed = new boolean[n];
        while (k > 0 && res.length() < n) {
            for (int d = 0; d < 10; d++) {
                if (indices.get(d).isEmpty()) continue;
                int pos = indices.get(d).peek();
                int cost = pos - tree.preSum(pos - 1);
                if (cost > k) continue;
                removed[pos] = true;
                k -= cost;
                res.append(d);
                indices.get(d).remove();
                tree.update(pos, 1);
                break;
            }
        }
        for (int i = 0; i < n; i++) {
            if (!removed[i]) res.append(num.charAt(i));
        }
        return res.toString();

    }

    class Fenwick {
        int[] tree;
        public Fenwick(int n) {
            tree = new int[n];
        }
        public void update(int index, int delta) {
            index++;
            while (index < tree.length) {
                tree[index] += delta;
                index += lowbit(index);
            }
        }
        public int preSum(int index) {
            int res = 0;
            index++;
            while (index > 0) {
                res += tree[index];
                index -= lowbit(index);
            }
            return res;
        }
        private int lowbit(int index) {
            return index & (-index);
        }
    }
}

