struct Node {
public:
    Node* left, *right;
    int l, r;
    int sum;

    Node(int _l, int _r): l(_l), r(_r), left(nullptr), right(nullptr), sum(0) {}
};

void add(Node* root, int val) {
    if (val < root->l || val > root->r) return;
    if (root->l == root->r) root->sum ++;    
    else {
        int mid = (root->l + root->r) / 2;
        if (val <= mid) {
            if (!root->left) root->left = new Node(root->l, mid);
            add(root->left, val);
        }

        else {
            if (!root->right) root->right = new Node(mid+1, root->r);
            add(root->right, val);
        }
        root->sum ++;
    }
} 

int find(Node* root, int val) {
    if (!root) return 0;
    if (val < root->l || val > root->r) return 0;
    if (val == root->r) return root->sum;

    int mid = (root->l + root->r) / 2;
    if (val <= mid) {
        return find(root->left, val);
    }

    return find(root->left, mid) + find(root->right, val);
}

class Solution {
public:
    string minInteger(string num, int k) {
        vector<queue<int>> record(10);
        int n = num.size();
        if(k > n*(n-1)/2) {
            sort(num.begin(), num.end());
            return num;
        }
        for (int i = 0; i < n; ++i) record[num[i]-'0'].push(i);

        string res;
        Node* root = new Node(0, n-1);
        while (n) {
            for (int i = 0; i <= 9; ++i) {
                if (record[i].size() > 0) {
                    int id = record[i].front();
                    int pre = find(root, id);
                    if (id - pre <= k) {
                        k = k - (id - pre);
                        res += (char)('0' + i);
                        add(root, id);
                        record[i].pop();
                        break;
                    }
                }
            }
            n --;
        }

        return res;
    }
};
```

