		res = 0
		while idx > 0:
			res += self.arr[idx]
			idx -= idx & -idx
		return res

class Solution:
	def minInteger(self, num: str, k: int) -> str:
		idmap = defaultdict(list)
		for i in range(len(num)):
			idmap[int(num[i])].append(i)

		tree = binaryIndexTree(len(num))
		res = ''
		for _ in range(len(num)):
			for i in range(10):
				if i in idmap:
					relevant = tree.query(idmap[i][0] + 1)
					val = idmap[i][0] - relevant
					if val <= k:
						k -= val
						tree.add(idmap[i].pop(0) + 1, 1)
						if not idmap[i]:
							del idmap[i]
						res += str(i)
						break

		return res

