class Solution {
    public String minInteger(String num, int k) 
    {

		HashMap<Integer,List<Integer>> digitVsPositionMap = new HashMap< Integer,List<Integer>> ();

		for (int i = 0; i <= 9; ++i) 
		{
			digitVsPositionMap.put(i, new LinkedList<>());
		}

		for (int i = 0; i < num.length(); ++i) 
		{
			int numberAtDigit =  Character.getNumericValue(num.charAt(i)) ;

			List<Integer> list = digitVsPositionMap.get(numberAtDigit);

			list.add(i);

			digitVsPositionMap.put(numberAtDigit, list);
		}

		String ans = "";


		int posArr[] = new int[num.length()];

		Arrays.fill(posArr, 0);

		SegmentTree tree = new SegmentTree(posArr);

		for (int i = 0; i < num.length(); ++i) 
		{

			for (int digit = 0; digit <= 9; ++digit) 
			{

				if (digitVsPositionMap.get(digit).size() != 0) 
				{

					Integer pos = digitVsPositionMap.get(digit).get(0);


					int shift = tree.sumRange( 0 , pos);


					if (pos - shift <= k) 
					{
						k -= pos - shift;

						tree.update(pos, 1);

						digitVsPositionMap.get(digit).remove(0);

						ans += digit;

						break;
					}
				}
			}
		}

		return ans;

    }
}

class SegmentTree 
{

	SegmentTreeNode root = null;

	public SegmentTree( int[] nums )
	{
		root = buildTree(nums, 0, nums.length-1);
	}


	private SegmentTreeNode buildTree(int[] nums, int start, int end) 
	{
		if (start > end)  // base case
		{
			return null;
		} 
		else 
		{
			SegmentTreeNode ret = new SegmentTreeNode(start, end);

			if (start == end)   // if start and end index are same , then we reached leaf nodes , here after no childs 
			{
				ret.sum = nums[start];
			} 
			else  // else there is child that we need to explore
			{
				int mid = start  + (end - start) / 2;             

				ret.left = buildTree(nums, start, mid);  // constructing left sub tree 

				ret.right = buildTree(nums, mid + 1, end); // constructing right sub tree

				ret.sum = ret.left.sum + ret.right.sum;  // updating the root sum 
			} 

			return ret;
		}
	}

	public void update(int i, int val) 
	{
        update(root, i, val);
    }

    private void update(SegmentTreeNode root, int pos, int val) 
    {
        if (root.start == root.end) 
        {
           root.sum = val;
        }
        else 
        {
            int mid = root.start + (root.end - root.start) / 2;  // find the mid index of the root node

            if (pos <= mid)   // if the index of the element ( for which we have to update value ) is less than mid , 
            {				  //then node lies in left subtree 
                 update(root.left, pos, val);
            } 
            else  //  or else it lies in right sub tree 
            {
                 update(root.right, pos, val);
            }

            root.sum = root.left.sum + root.right.sum; // after exploring  we are updating the root element sum , since it child values changed
        }
    }

    public int sumRange(int i, int j) 
    {
        return sumRange(root, i, j);
    }

    private int sumRange(SegmentTreeNode root, int start, int end) 
    {
        if (root.end == end && root.start == start) 
        {
            return root.sum;
        } 
        else 
        {
            int mid = root.start + (root.end - root.start) / 2;

            if (end <= mid)    
            {
                return sumRange(root.left, start, end);
            } 
            else if (start >= mid+1) 
            {
                return sumRange(root.right, start, end);
            }  
            else 
            {    
                return sumRange(root.right, mid+1, end) + sumRange(root.left, start, mid);
            }
        }
    }

	class SegmentTreeNode 
	{
        int start, end;
        SegmentTreeNode left, right;
        int sum;

        public SegmentTreeNode(int start, int end) {
            this.start = start;
            this.end = end;
            this.left = null;
            this.right = null;
            this.sum = 0;
        }
    }
}

