	var minInteger = function (num, k) {
        if (!num || num.length === 0 || k <= 0) return num;
        num = num.split("").map(Number);
        let len = num.length;
        let l = 0,
          index = 0,
          smallest = Infinity;
        while (k > 0 && l < len - 1) {
          smallest = Infinity;
          for (let i = l; i < len && i <= l + k; i++) {
            if (num[i] < smallest) {
              smallest = num[i];
              index = i;
            }
          }
          for (let j = index; j > l; j--) {
            [num[j], num[j - 1]] = [num[j - 1], num[j]];
            k--;
            if (k <= 0) {
              break;
            }
          }
          l++;
        }
        return num.join("");
      };

