        Node(final int val) {
            this.val = val;
        }

        Node addNext(final int i) {
            final Node next = new Node(i);
            this.nextNode = next;
            next.prevNode = this;
            return next;
        }

        void addPrev(final Node node) {
            final Node prev = this.prevNode;
            node.prevNode = prev;

            if (prev != null) {
                prev.nextNode = node;
            }

            node.nextNode = this;
            this.prevNode = node;
        }

        void unlink() {
            final Node prev = this.prevNode;
            final Node next = this.nextNode;
            this.prevNode = null;
            this.nextNode = null;
            prev.nextNode = next;

            if (next != null) {
                next.prevNode = prev;
            }
        }

        @Override
        public String toString() {
            Node current = this;
            final StringBuilder sb = new StringBuilder();

            while (current != null) {
                sb.append(current.val);
                current = current.nextNode;
            }

            return sb.toString();
        }

    }

    private boolean increasing(final String num) {
        for (int i = 0; i < num.length() - 1; i++) {
            if (num.charAt(i) > num.charAt(i + 1)) {
                return false;
            }
        }

        return true;
    }

    public String minInteger(final String num, final int k) {
        if (increasing(num)) {
            return num;
        }

        final Node head = new Node(num.charAt(0) - '0');
        Node tail = head;

        for (int i = 1; i < num.length(); i++) {
            tail = tail.addNext(num.charAt(i) - '0');
        }

        final Node newHead = swap(head, k);
        return newHead.toString();
    }

    private Node swap(final Node head, final int remaining) {
        if (remaining == 0 || head == null) {
            return null;
        }

        if (head.val == 0) {
            swap(head.nextNode, remaining);
            return head;
        }

        Node nextSmallest = null;
        int nextSmallestDistance = 0;

        Node current = head.nextNode;
        int distance = 1;

        while (current != null && distance <= remaining) {
            if (nextSmallest == null || current.val < nextSmallest.val) {
                nextSmallest = current;
                nextSmallestDistance = distance;
            }

            current = current.nextNode;
            distance++;
        }

        if (nextSmallest != null && nextSmallest.val < head.val) {
            nextSmallest.unlink(); // O(1)
            head.addPrev(nextSmallest); // O(1)
            swap(head, remaining - nextSmallestDistance);
            return nextSmallest;
        }

        swap(head.nextNode, remaining);
        return head;
    }

    @Test
    public void test() {
        assertEquals("012345", new Solution().minInteger("012345", 23));
        assertEquals("0345989723478563548", new Solution().minInteger("9438957234785635408", 23));
        assertEquals("1342",new Solution().minInteger("4321", 4));
        assertEquals("010",new Solution().minInteger("100", 1));
        assertEquals("36789",new Solution().minInteger("36789", 1000));
        assertEquals("22",new Solution().minInteger("22", 22));
        assertEquals("124498948179",new Solution().minInteger("294984148179", 11));
    }

}
