function FenwickTree(n) {
	this.sum = Array(n+1).fill(0);
}

FenwickTree.prototype.update = function(i, delta) {
	while (i < this.sum.length) {
		this.sum[i] += delta;
		i += i & (-i);
	}
}

FenwickTree.prototype.query = function(i) {
	let sum = 0;
	while (i > 0) {
		sum += this.sum[i];
		i -= i & (-i);
	}

	return sum;
}

var minInteger = function(num, k) {
	const n = num.length;
	const pos = {}

	for (let i = 0; i < n; i++) {
		pos[num[i]] = pos[num[i]] || []
		pos[num[i]].push(i);
	}

	const fenwickTree = new FenwickTree(n);
	const used = new Set();
	let ans = '';

	while (k > 0 && ans.length < n) {
		for (let d = 0; d < 10; d++) {
			if (!pos[d+''] || !pos[d+''].length) continue;
			const i = pos[d+''][0];
			const cost = i - fenwickTree.query(i);
			if (cost > k) continue;
			k -= cost;
			ans += d;
			fenwickTree.update(i+1, 1);
			used.add(i);
			pos[d+''].shift();
			break;
		}
	}

	for (let i = 0; i < n; i++) {
		if (!used.has(i)) ans += num[i];
	}

	return ans;
};

