class Solution {
    public String minInteger(String num, int k) {
        List<Queue<Integer>> list = new ArrayList<>();
        for (int i = 0; i <= 9; i++) {
            list.add(new LinkedList<>());
        }
        for (int i = 0; i < num.length(); i++) {
            list.get(num.charAt(i) - '0').offer(i);
        }

        Tree tr = new Tree(num.length());
        StringBuilder sb = new StringBuilder();
        for (int i = 0; i < num.length(); i++) {
            for (int j = 0; j <= 9; j++) {
                if (list.get(j).size() != 0) {
                    Integer pos = list.get(j).peek();
                    int shift = tr.getLowerSum(pos);
                    if (pos - shift <= k) {
                        list.get(j).poll();
                        k -= pos -shift;
                        tr.add(pos);
                        sb.append(j);
                        break;
                    }
                }
            }
        }
        return sb.toString();
    }

    public class Tree{
        int[] nodes;
        int n;

        public Tree(int max) {
            nodes = new int[4 * max];
            n = max;
        }

        public void add(int num) {
            add(num, 0, n, 0);
        }

        private void add(int num, int l, int r, int node) {
            if (num < l || num > r) return;
            if (l == r) {
                nodes[node]++;
                return;
            }
            int mid = (r + l) / 2;
            add(num, l, mid, node * 2 + 1);
            add(num, mid + 1, r, node * 2 + 2);
            nodes[node] = nodes[node * 2 + 1] + nodes[node * 2 + 2];
        }

        public int getLowerSum(int pos) {
            return getSum(0, pos, 0, n, 0);
        }

        private int getSum(int ql, int qr, int l, int r, int node) {
            if (ql > r || qr < l) return 0;
            if (ql <= l && qr >= r) return nodes[node];
            int mid = (r + l) / 2;
            return getSum(ql, qr, l, mid, node * 2 + 1) + getSum(ql, qr, mid + 1, r, node * 2 + 2);
        }
    }
}

