class FenwickArray {
private:
    vector<int> array;

public:
    FenwickArray(int n): array(vector<int>(n + 1, 0)) {}

    int query(int idx) {
        int ans = 0;
        while (idx > 0) {
            ans += array[idx];
            idx -= idx & -idx;
        }
        return ans;
    }

    void add(int idx, int delta) {
        while (idx < array.size()) {
            array[idx] += delta;
            idx += idx & -idx;
        }
    }
};

