	vector<int> curr(digits); // stores the head of each queue

	vector<vector<int>> deltas(digits);

	for(int i = 0; i < n; ++i)
	{
		queues[num[i]-'0'].push_back(i);
		deltas[num[i]-'0'].push_back(0);
	}

	string prefix;
	vector<int> used(n);

	// Put some sentinels to ease bunary search
	for(int d = 0; d < digits; ++d)
	{
		queues[d].push_back(INT_MAX);
		deltas[d].push_back(0);            
	}

	while (k > 0)
	{
		bool found = false;

		for(int d = 0; d < digits; ++d)
		{
			int& p = curr[d];
			if (p < queues[d].size())
			{
				int delta = queues[d][p] + deltas[d][p];
				if (delta <= k)
				{
					int pos = queues[d][p];
					used[pos] = true;

					prefix.push_back('0' + d);

					++p;
					if (p < queues[d].size())
					{
						deltas[d][p] += deltas[d][p-1];
						deltas[d][p] -= 1;
					}

					found = true;
					k -= delta;

					for(int t = 0; t < digits; ++t)
					{
						if (t == d)
						{
							continue;
						}


						int low = curr[t]-1, high = queues[t].size()-1;
						while (low + 1 < high)
						{
							int m = (low + high) / 2;    

							if (queues[t][m] > pos)
								high = m;
							else
								low = m;
						}

						deltas[t][high]--;

