class Solution {
    public String minInteger(String num, int swaps) {
        char[] arr = num.toCharArray();
        arr = minArray(arr, arr.length, swaps); 
        return new String(arr);
    }
    char[] minArray(char[] arr, int length, int swaps) { 
        if (swaps == 0) 
            return arr; 
        for (int i = 0; i < length; i++) { 
            int min_index = 0, min = Integer.MAX_VALUE;
            int limit = (swaps+i) > length-1 ?  length-1 : swaps + i; 
            for (int j = i; j <= limit; j++) {
                if (arr[j] < min) { 
                    min = arr[j]; 
                    min_index = j; 
                }
            }
            swaps -= (min_index - i);     
            arr = swapMin(arr, i, min_index); 
            if (swaps == 0) 
                break; 
        }
        return arr;
    } 
    char[] swapMin(char[] arr, int target, int current) { 
        char temp = '0'; 
        for (int i = current; i > target; i--) { 
            temp = arr[i - 1]; 
            arr[i - 1] = arr[i]; 
            arr[i] = temp; 
        }
        return arr;
    } 
}
