class Solution {

    public String minInteger(String num, int k) {

		// the k enough to make the string ordered
        if(k>=num.length()*(num.length()-1)/2){
            char[] cs = num.toCharArray();
            Arrays.sort(cs);
            return new String(cs);
        }

        StringBuilder sb = new StringBuilder(num);
        StringBuilder rst = new StringBuilder();

        while(k>0){
            for(int i=0; i<9; i++){
                int idx = sb.indexOf(String.valueOf(i));
				// swap the char to the head
                if(idx>=0 && idx<=k){
                    k-=idx;
                    rst.append(i);
                    sb.deleteCharAt(idx);
                    i=-1;
                }
            }
            break;
        }

		// if the k still>0, move the first non ordered char k forward
        if(k>0){
            for(int i=1; i<sb.length(); i++){
                if(sb.charAt(i)<sb.charAt(i-1)){
                    char ch = sb.charAt(i);
                    sb.deleteCharAt(i);
                    sb.insert(i-k, ch);
                    break;
                }
            }
        }

        rst.append(sb);
        return rst.toString();

    }
}
