from queue import deque


class Solution:
    def minInteger(self, num: str, k: int) -> str:
        n = len(num)
        nums = [int(c) for c in num]
        N = n+1
        cnts = [0]*N

        def update(pos, v):
            i = pos+1
            while i < N:
                cnts[i] += v
                i += i & (-i)

        def getSum(pos):
            i = pos+1
            ans = 0
            while i > 0:
                ans += cnts[i]
                i -= i & (-i)
            return ans

        left = [-1]*10
        for i, v in enumerate(nums):
            if left[v] != -1:
                continue
            left[v] = i
        ans = []
        res = k
        i = 0
        while res > 0 and i < n:
            cur = nums[i]
            flg = False
            for v in range(cur):
                if left[v] == -1:
                    continue
                pos = left[v]
                cost = pos-getSum(pos)
                if cost > res:
                    continue
                flg = True
                res -= cost
                ans.append(v)
                nums[pos] = -1
                update(pos, 1)
                left[v] = -1
                for ni in range(pos+1, n):
                    if nums[ni] == v:
                        left[v] = ni
                        break
                break
            if not flg:
                ans.append(cur)
                nums[i] = -1
                update(i, 1)
                while i < n and nums[i] == -1:
                    i += 1
                if left[cur] != -1:
                    left[cur] = -1
                    for ni in range(i, n):
                        if nums[ni] == cur:
                            left[cur] = ni
                            break
        while i < n:
            if nums[i] != -1:
                ans.append(nums[i])
            i += 1
        return "".join([str(v) for v in ans])

