        List<Integer> exclude = new ArrayList<>();

        while(!queue.isEmpty() && queue.peek().dist <= k) {
            Item item = queue.poll();
            sb.append(item.val);
            int insertPos = bisearch(exclude, item.dist);
            exclude.add(insertPos, item.dist);
            k -= Math.max(0, (item.dist - insertPos));
        }
        if(!exclude.isEmpty()) {
            String next = throwaway(num, exclude);
            return sb.toString() + minInteger(next, k);
        }

        while(!queue.isEmpty() && queue.peek().dist > k) queue.poll();
        Item item = queue.peek();
        return "" + item.val + minInteger(num.substring(0, item.dist) + num.substring(item.dist+1), k-item.dist);
    }

    private int bisearch(List<Integer> list, int i) {
        int index = Collections.binarySearch(list, i);
        if(index < 0) index = -index-1;
        return index;
    }

    private String throwaway(String s, List<Integer> exclude) {
        Set<Integer> set = new HashSet<>(exclude);
        StringBuilder sb = new StringBuilder();
        for(int i = 0; i < s.length(); i++) {
            if(set.contains(i)) continue;
            sb.append(s.charAt(i));
        }
        return sb.toString();
    }
}
