class Solution {
    public String minInteger(String s, int k) {
        char A[]=s.toCharArray();
        for(int i=0;i<A.length;i++){
            char min=A[i];
            int j=i+1;
            int copyk=k;
            int index=-1;
            while(copyk>0&&j<A.length){
                if(A[j]<min){
                    min=A[j];
                    index=j;
                }
                copyk--;j++;
            }
            if(index==-1||min==A[i])continue;
            for(j=index;j>i;j--){
                swap(A,j,j-1);
            }
            k-=(index-i);
            if(k==0)break;
        }
        StringBuilder str=new StringBuilder();
        for(char c:A)str.append(c+"");
        return str.toString();
    }

    public void swap(char A[],int i,int j){
        char temp=A[i];
        A[i]=A[j];
        A[j]=temp;
    }
}
