class Node:
    def __init__(self, left, right, l, r, val, cnt, idx):
        self.left = left
        self.right = right
        self.l = l
        self.r = r
        self.val = val
        self.cnt = cnt
        self.idx = idx
NONE_NODE = Node(None, None, -1, -1, float('inf'), 0, -1)
class SegmentTree:
    def __init__(self, nums):
        self.head = self.build(nums, 0, len(nums)-1) 

    def build(self, nums, l, r):
        if l == r:
            return Node(None, None, l, r, nums[l], 1, l)
        mid = (l+r)//2
        left = self.build(nums, l, mid)
        right = self.build(nums, mid+1, r)
        return Node(left, right, l, r, min(left.val, right.val), left.cnt+right.cnt, left.idx if left.val <= right.val else right.idx)
    def remove(self, u, node):
        if node == None or u == node.l == node.r:
            return None
        if node.r < u or u < node.l:
            return node

        node.left = left = self.remove(u, node.left)
        node.right = right = self.remove(u, node.right)
