class Solution {
    vector<int> v;

    int lowbit(int x) {
        return x & (-x);
    }

    void update(int x, int y) {
        for (int i = x; i < v.size(); i += lowbit(i)) {
            v[i] += y;
        }
    }

    int get(int x) {
        int r = 0;
        for (int i = x; i; i ^= lowbit(i)) {
            r += v[i];
        }
