        v.resize(n + 1);
        vector<list<int>> d(10);
        for (int i = 0; i < n; ++i) {
            update(i + 1, 1);
            d[num[i] - '0'].push_back(i);
        }
        string r(n, ' ');
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < 10; ++j) {
                if (!d[j].empty()) {
                    const int x = get(*d[j].begin());
                    if (x <= k) {
                        k -= x;
                        r[i] = j + '0';
                        update(1 + *d[j].begin(), -1); 
                        d[j].pop_front();
                        break;
                    }

                }
            }
        }
        return r;
    }
};
