class Solution {
public:
    string minInteger(string num, int k) {
        int n = num.size();
        if(k == 0)
            return num;
        int t;
        string ans = "";
        int cur[30010] = {0};
        for(t = 0; t < 10; t++){
            cur[0] = 0;
            int ind = 0;
            for(int i = 0; i < num.size(); i++){
                if(i > 0)
                    cur[i] = cur[i-1];
                if(num[i] == t+'0'){
                    if(i - cur[i] <= k){
                        ans += t+'0';
                        k -= i - cur[i];
                        cur[i]++;
                        ind = i+1;
                    }
                    else{
                        ind = i;
                        break;
                    }
                }
                if(k == 0)
                    break;
            }
            string tmp = "";
            for(int i = 0; i < ind; i++)
                if(num[i] != t + '0')
                    tmp += num[i];
            int l = tmp.size();
            for(int i = ind; i < num.size(); i++)
                tmp += num[i];
            num = tmp;
            if(k == 0)
                return ans + num;
            ind = -1;
            for(int i = l; i < num.size(); i++)
                if(num[i] == t + '0'){
                    ind = i;
                    break;
                }
            if(ind > 1){
                bool rise = true;
                for(int i = 1; i < ind - k; i++)
                    if(num[i] < num[i-1]){
                        rise = false;
                        break;
                    }
                for(int i = 0; i < k; i++){
                    if(num[ind-k-1] > num[ind-k+i]){
                        rise = false;
                        break;
                    }
                }
                if(rise){
                    for(int i = 0; i < k; i++){
                        char cc = num[ind-i];
                        num[ind-i] = num[ind-i-1];
                        num[ind-i-1] = cc;
                    }
                    return ans + num;
                }
            }
            if(k == 0){
                ans += num;
                break;
            }
        }

        return ans;
    }
};
