 fun minInteger(num: String, k: Int): String {
        val arr = num.toCharArray()
        helper(arr, 0, k)
        return arr.joinToString("")
    }

    fun helper(arr: CharArray, index: Int, k: Int) {
        var count = k        
        for(index in 0 until arr.size) {
		    // Get the min value out of an arr from index + 1 to min(remaining k, arr.size)
            var minIndex = index
            for(i in index + 1 until Math.min(arr.size, index + count + 1)) {
                if(arr[i] < arr[minIndex]) {
                    minIndex = i
                }
            }
			// After finiding minimum value adjust items
            val temp = arr[minIndex]
            for(j in minIndex downTo index + 1) {
                arr[j] = arr[j - 1]
            }
            arr[index] = temp
			// reduce count by number of used for above operation.
            count -= (minIndex - index)
        }
    }

