132 ms.
we search from  0 - 9 to find the next number with range k greedly.
Sort previus number in a set.  keep a dis[10] array to record each number's distance.  keep pos[10] array to track each number's next position.
Trick is we need to update dis[10] each time we find a number v in front postion curpos.
for other 9 number v1 that is not v, just compare position pos[v1]  with curpos, add 1 if cupos is bigger than pos[v1].
for number v,  we need to caulate distance 

                            auto iter1 = m.lower_bound(pre);
                            auto iter2 = m.lower_bound(pos[v]);
                            int offset = std::distance(iter1, iter2);
                            dis[v] = dis[v] - offset;
Note this is (much) faster than std::distance(iter2, m.end());

time O(nlogn)


```
class Solution {
public:

    int next(int cur, string& num)
    {
        int v = num[cur] - '0';
        for (int i = cur + 1; i < num.size(); i++)
        {
            int nv = num[i] - '0';
            if (nv == v)
            {
                return i;
            }
        }
        return -1;
    }
    string minInteger(string num, int k) {
        vector<int> pos(10, -1);
        vector<int> dis(10, 0);
        for (int i = 0; i < num.size(); i++)
        {
            int v = num[i] - '0';
            if (pos[v] == -1)
            {
                pos[v] = i;
            }
        }
        int i = 0;
        vector<int> res;
        set<int> m;
        while (k > 0)
        {
            bool find = false;
            int v = 0;
            int cur;
            for (v = 0; v < 10; v++)
            {
                cur = pos[v];
                if (cur != -1 )
                {
                    if ((cur + dis[v] - i) <= k)
                    {
                        int pre = pos[v];
                        find = true;
                        res.push_back(v);
                        k = k - (cur + dis[v] - i);
                        pos[v] = next(cur, num);
                        if (pos[v] != -1)
                        {
                            auto iter1 = m.lower_bound(pre);
                            auto iter2 = m.lower_bound(pos[v]);
                            int offset = std::distance(iter1, iter2);
                            dis[v] = dis[v] - offset;
                        }
                        m.insert(cur);
                        break;
                    }
                }
            }

            i++;
            if (!find) break;
            for (int t = 0; t < 10; t++)
            {
                if (t != v)
                {
                    if (pos[t] < cur)
                    {
                        dis[t]++;
                    }
                }
            }

        }

        bool allend = false;
        while (true)
        {
            int minpos = INT_MAX;
            int minv = -1;
            int v = -1;
            for (v = 0; v < 10; v++)
            {
                if (pos[v] != -1 && minpos > (pos[v]))
                {
                    minpos = pos[v];
                    minv = v;
                }
            }

            if (minpos == INT_MAX)
            {
                break;
            }
            pos[minv] = next(minpos, num);
            res.push_back(minv);
        }
        string re;
        re.resize(res.size());
        for (int i = 0; i < res.size(); i++)
        {
            re[i] = res[i] + '0';
        }
        return re;
    }
};
