        char[] ar = num.toCharArray();
        int i =0;
        while(k > 0){
            int idx = getMinNumberIdx(ar, i, k);
            if(idx==-1)break; //whole array is sorted.

            char min = ar[idx];
            for(int j=idx; j >i; j--){
                ar[j] = ar[j-1]; 
            }
            ar[i] = min;

            k -= (idx-i); 

            i++;

        }
        return new String(ar);
    }
