#include <ext/pb_ds/assoc_container.hpp> 
#include <ext/pb_ds/tree_policy.hpp> 
using namespace __gnu_pbds; 
class Solution {

public:
    #define pr pair<int,int>
    #define pb push_back
    #define fi first
    #define se second


    #define ordered_set tree<int, null_type,less<int>, rb_tree_tag,tree_order_statistics_node_update>


    struct compare_queue
    {
        bool operator()(const int& a, const int& b)
        {
            return a>b;
        }
    };

    string minInteger(string num, int k) {
            vector<priority_queue<int,vector<int>,compare_queue>  > q(10);              int n=num.size();
        int i,j;
        for(i=0;i<n;i++){
            q[num[i]-'0'].push(i);
        }
        i=0;
        string ans="";
        ordered_set po;
        while(i<n){
            if(num[i]=='X'){i++; continue;}
            if(k==0){
                ans+=num[i];
                i++;
                continue;
            }
            int curr=num[i]-'0';
            int flag=0;
            for(j=0;j<curr;j++){

                while(!q[j].empty()  &&  q[j].top()<i){q[j].pop();}
                if(q[j].empty()){continue;}
                int val1=po.order_of_key(q[j].top());
               int val2=po.order_of_key(i);
                val1=val1-val2;
                val1=q[j].top()-i-val1;

                if(k>=val1){
                    flag=1;
                    k-=val1;
                    ans+=num[q[j].top()];
                    po.insert(q[j].top());
                    num[q[j].top()]='X';
                    q[j].pop();
                    break;
                }
            }
            if(flag==0){
                ans+=num[i];
                i++;
            }
        }

        return ans;


    }
};
