```javascript
// pseudo code:
// put number into a data structure that reduces the need for unnecessary copying
// iterate through the datastructure and find the minimum value we can put into the digit
// with the greatest weight with the minimum number of moves
// move that value to the current digit while shifting all digits from the digit to the minimum digit to the right
// decrement k by the amount of digits we moved

var minInteger = function(num, k) {
    const numArr = num.split('');
    for (let i = 0; i < num.length && k; i++) {
        let swapIdx = i;
        const end = Math.min(num.length, k + i + 1);
        for (let j = i + 1; j < end; j++) {
            if (numArr[swapIdx] > numArr[j]) swapIdx = j;
        }
        const temp = numArr[swapIdx];
        for (let k = swapIdx; k > i; k--) {
            numArr[k] = numArr[k - 1];
        }
        numArr[i] = temp;
        k -= swapIdx - i;
    }
    return numArr.join('');
};
```

