Space: O(1)
```
class Solution {
public:
    bool ispossible(int mid, vector<int>& dist, double &hour, int n){
    /*  For the first n - 1 elements it will be ceiling value, 
        as we need to wait for the next train.
        But for the last n'th element it will be floating value, 
        as we dont need to wait for next train.   */
        int res = 0;
        for(int i = 0; i < n - 1; i++)
            res += ((dist[i] + mid - 1) / mid);
        double time = (dist[n - 1] * 1.0) / mid + res;
        return time <= hour;
    }
    int minSpeedOnTime(vector<int>& dist, double hour) {
        const int n = dist.size();
        int low = 1;
        int high = 10000000;//as the speed can go as high as 10^7
        int ans = -1;
        while(low <= high){
            int mid = low + (high - low) / 2;
            if(ispossible(mid, dist, hour, n)){
                ans = mid;
                high = mid - 1;
            }
            else
                low = mid + 1;
        }
        return ans;
    }
};
```

