class Solution {
public:
    double func(vector<int>&dist,int k)
    {
        double ans=0;
        int n = dist.size();
        for(int i=0;i<n;i++)
        {
            if(i==n-1)
            {
                ans+= (1.0*dist[i])/k;
            }
            else
            {
                ans += dist[i]/k;
                if(dist[i]%k>0)
                ans++;
            }
        }
        return ans;
    }
    int minSpeedOnTime(vector<int>& dist, double hour) 
    {
        int s = 1,e = 1e9;
        int ans = e;
        while(s<=e)
        {
            int mid = s + (e-s)/2;
            double x = func(dist,mid);
            if(x<=hour)
            {
                ans = min(ans,mid);
                e = mid-1;
            }
            else
            {
                s = mid+1;
            }
        }
        return ans==e?-1:ans;
    }
};
```
