            for(int i = 0; i < n - 1; i++) total_time += Math.Ceiling((double)dist[i] / mid);
            total_time += (double)dist[n - 1] / mid;
            if(total_time <= hour) right = mid;
            else left = mid + 1;
        }
        return left;
    }
}
