# Code
```
public class Solution {
    public int MinSpeedOnTime(int[] dist, double hour) {
        //Cannot reach in time if hour's integer part is less than
        // array length since every train takes at least one hour to 
        // start
        if(dist.Length > Math.Ceiling(hour)) return -1;

        //Using Binary search to find speed. Upper limit is given in 
        // the question. Lower limit is anyway 1 (since output is int)
        int lSpeed = 1; int rSpeed = 10000000;
        while(lSpeed < rSpeed){
            int m = lSpeed + (rSpeed - lSpeed)/2;
            double outTime = TimeRequired(dist, m);

            //Checking binary search condition
            if(outTime > hour){
                lSpeed = m + 1;
            } 
            else{
                rSpeed = m;
            } 
        }

        //By the end, we would have found the optimal speed
        return lSpeed; 
    }

    private double TimeRequired(int[] dist, int speed){
        double outTime = 0;
        //Calculating int upper values of all but last dist[i] since each train leaves at the hour only
        for(int i = 0; i < dist.Length - 1; i++){
            outTime += Math.Ceiling((double)dist[i]/speed);
        }
        //Last dist entry can be float since last stop is right at our office. 
        outTime += (double)dist[dist.Length - 1]/speed;

        return outTime;
    }
}
```

