```
class Solution {

    func minSpeedOnTime(_ dist: [Int], _ hour: Double) -> Int {
        if Double(dist.count) - 1 > hour {
            return -1
        }

        var s = 1
        var e = 1000000001
        var minTime = e

        while s <= e {
            var mid = (s + e) / 2
            if canReach(mid) {
                minTime = min(minTime, mid)
                e = mid - 1
            } else {
                s = mid + 1
            }
        }

        func canReach(_ speed: Int) -> Bool {
            var time = 0.0
            for i in 0 ..< dist.count - 1 {
                time += ceil(Double(dist[i]) / Double(speed))
            }
            time += (Double(dist.last!) / Double(speed)) // last one doesn't need ceil up as you dont need to wait for another train!
            return time <= hour
        }

        return minTime == 1000000001 ? -1 : minTime
    }
}
