    long long r = 10000000; // Maximum speed (upper bound) for binary search
    int ans = -1; // Initialize the answer to -1, indicating no valid speed found yet.

    while (l <= r) {
        long long m = (l + r) / 2; // Midpoint of the current search space

        // Calculate the total time required to travel at speed m
        double total_time = 0;
        for (int i = 0; i < n - 1; i++) {
            total_time += dist[i] / m; // Time for the current segment
            if (dist[i] % m) {
                total_time++; // Add an additional hour if there is a remainder
            }
        }
        total_time += (double)dist[n - 1] / m; // Time for the last segment

        if (hour >= total_time) {
            // If the total time required at speed m is less than or equal to the given hour,
            // then update the answer and search for a smaller speed.
            ans = m;
            r = m - 1;
        } else {
            // If the total time required at speed m is more than the given hour,
            // then search for a larger speed.
            l = m + 1;
        }
    }

    return ans; // Return the minimum speed required to complete the journey on time.
}
};
