class Solution {
    int n;
    public int minSpeedOnTime(int[] dist, double hour) {
        n = dist.length;
        if(n-1+1e-7>hour) return -1;
        int l = 1;
        int r = (int)1e7;
        int m = 0;
        while(l<r) {
            m = l + (r-l)/2;
            if(isPossible(dist, hour, m)) {
                r = m;
            } else {
                l = m+1;
            }
        }

        return l;
