    public boolean isPossible(int[] dist, double hour, int s) {
        int h = 0;
        for(int i=0; i<n-1; i++) {
            h += (dist[i]+s-1)/s;
        }
        double dh = h+(1d*dist[n-1])/s;
        return dh<=hour;
    }
}
