var minSpeedOnTime = function(dist, hour) {
    const lastTime = hour - (dist.length - 1);

    if (lastTime <= 0) {
        return -1;
    }

    const minSpeed = dist.reduce((r, d, i) => i < dist.length - 1 ? Math.max(r, d) : r, 0);
    const lastSpeed = dist[dist.length - 1] / lastTime;

    let left = 1;
    let right = Math.ceil(minSpeed + lastSpeed);

    while (left <= right) {
        const speed = Math.floor((left + right) / 2);

        const time = dist.reduce((res, d, i) => {
            let hours = d / speed;

            if (i < dist.length - 1) {
                hours = Math.ceil(hours);
            }

            return res + hours;
        }, 0);

        if (time <= hour) {
            right = speed - 1;
        } else {
            left = speed + 1;
        }
    }

    return left;
};
```

