 fun minSpeedOnTime(dist: IntArray, hour: Double): Int {
        val given = DoubleArray(dist.size) { i -> dist[i].toDouble() }
        fun isPossible(speed: Int): Boolean {
            var time = 0.0
            for (d in given) {
                time += Math.ceil(d / speed)
            }
            time -= Math.ceil(given.last() / speed) // don't wait for last train ride
            time += (given.last() / speed) // need to add floating point wait time for last ride
            return time <= hour
        }
        var (low, high) = 1 to 100000000
        while (low < high) {
            val mid = (low + high) / 2
            if (isPossible(mid).not())
                low = mid + 1
            else
                high = mid
        }
        if (isPossible(low)) // make sure the value is correct
            return low
        return -1
    }
