class Solution {
    func minSpeedOnTime(_ dist: [Int], _ hour: Double) -> Int {
        let n = dist.count, maxDist = dist.max()!
        let intHour = Int(hour.rounded(.up)), lastHour = hour == hour.rounded(.down) ? 1.0 : (hour - hour.rounded(.down))
        guard intHour >= n else { return -1 }
        var left = 1, right = intHour > n ? maxDist : max(maxDist, Int((Double(dist.last!) / lastHour).rounded(.up)))
        while left < right {
            let center = (left + right) / 2
            var totalTime = 0.0
            for i in 0..<(n - 1) {
                totalTime += (Double(dist[i]) / Double(center)).rounded(.up)
            }
            totalTime += Double(dist.last!) / Double(center)
            if totalTime > hour {
                left = center + 1
            } else {
                right = center
            }
        }
        return left
    }
}
