    double timetaken(vector<int>&dist,int s)
    {
        double res=0;
        for(int i=0; i<dist.size()-1; i++)
        {
            if(dist[i]%s==0)
                res+=dist[i]/s;
            else
                res+=dist[i]/s+1;
        }
        res+=(double)dist[dist.size()-1]/(double)s;
        return res;
