           mid=lo+(hi-lo)/2;
           tm=timetaken(dist,mid);
           if(tm<=hour)
           {
               res=mid;
               hi=mid-1;
           }
           else
           {
               lo=mid+1;
           }
       }
        return res;

    }
};
