class Solution {
public:
    bool check(double m,vector<int>& dist, double hour){
        double tot=0.0;int n=dist.size();
        for(int i=0;i<n-1;i++){
            tot+=ceil((double)(dist[i]/m));
        }
        tot+=(double) dist[n-1]/m;
        return hour>=tot;
    }
    int minSpeedOnTime(vector<int>& dist, double hour) {
        int l=1,h=1e9;int ans=-1;
        int n=dist.size();
        if(hour <= n-1) return -1;
        while(l<=h){
            double m=(l+h)>>1;
            if(check(m,dist,hour)){
                h=m-1;
                ans=m;
            }
            else{
                l=m+1;
            }
        }
        return ans;
    }
};
```

