import math

class Solution:
    def minSpeedOnTime(self, dist: List[int], hour: float) -> int:
        len_path = len(dist)
        max_dist = max(dist)
        # Paths that will have to the next full hour takes 1 hour each
        if len_path - 1 >= hour:
            return -1
        # If # of paths is equal to hours, they must last at most 1 hour
        if len_path == hour:
            return max_dist
        # If # of paths is equal to # of full hours, the time will be given by the last path
        if len_path - 1 == int(hour):
            return max(max_dist,round(dist[-1]/(hour-int(hour))))
        # Computing the least vel that would work if dist.length = 1
        vel = sum(dist)/hour
        # If this vel is lower than 1, means that the minimum valid vel works
        if vel <= 1:
            return 1
        # Computing a velocity that we are sure doesn't work, to the binary search
        vel = math.floor(vel) - 1
        left = vel              #Granting left will be the highest that doesn't work until now
        right = max_dist        #Granting right will be the lowest that worked until now
        aux = (left+right)//2
        while True:
            dur = 0
            # Testing the velocity
            for dis in dist:
                dur += dis/aux
                if dur > hour:
                    dur = -1
                    break
                else:
                    dur = math.ceil(dur)
            if dur == -1:
                left = aux
            else:
                right = aux
            # Ends when left is adjacent to right
            if left == right - 1:
                break
            aux = (left + right)//2

        return right

```

