class Solution {
public:
    bool cond(int &speed,vector<int>& dist, double &hour)
    {
        double time=0;
        for(int i=0;i<dist.size()-1;i++)
        {
            time+=dist[i]/speed;                // it adds integer values on dividing
            time+=(dist[i]%speed==0)?0:1;       // eg if 3/2=1.5 so in int 1.5 =1 so for extra 0.5 we add 1 bcz train comes at mark 2 so time it would take in total is 2 not 1 if decimal value add 1;
        }
        time+=(dist[dist.size()-1]*1.0)/speed;  // last distance is deciding weather we will be before time or not
        return time<=hour;                    // true or false it returns
    }

    int minSpeedOnTime(vector<int>& dist, double hour) {
        if((double)dist.size()-1>=hour) return -1;

        int left=1;
        int right=1e7;  // will check on all range using binary search algo

        while(left<=right)
        {
            int mid=(left+right)/2;

            if(cond(mid,dist,hour))
            {
                right=mid-1;
            }
            else {
                left=mid+1;
            }
        }
        return left;
    }
};
