class Solution {
public:
    int minSpeedOnTime(vector<int>& dist, double hour) {
        double k=0;
        int n=dist.size();
        for(int i=0;i<n;i++){
            k+=dist[i];
        }
        int ans=-1;
        if(n-1>hour)return -1;
        if(k==hour)return 1;
        int st=1;
        int en=1e7;

        while(st<=en){
            int mid=(st+en)/2;
            double l=0;
            for(int i=0;i<n;i++){
                if(i!=n-1){
                    if(dist[i]%mid==0){
                        l+=dist[i]/mid;
                    }
                    else{
                        l+=(dist[i]+mid)/mid;
                    }
                }
                else{
                    l+=(double)(dist[i])/(double)(mid);
                }
            }
            if(l<=hour){
                ans=mid;
                en=mid-1;

            }
            else{
                st=mid+1;
            }
        }
        return ans;
    }
};
