        for(int i=0;i<dist.size()-1;i++){
            hours+=dist[i]/mid;
            if (dist[i] % mid != 0)
                hours++; 
        }

        hours+=dist[dist.size()-1]/(double)mid; //for the last index we need the exact hours so we are calculating it separately

        return hours<=hour;
    }
    int minSpeedOnTime(vector<int>& dist, double hour) {
        int low=1,high=1000000;
        int ans=INT_MAX;

        while(low<=high){
            int mid=low+(high-low)/2;
            if(isPossible(dist,mid,hour)){
                ans=mid;
                high=mid-1;
            }
            else{
                low=mid+1;
            }
        }

        return ans==INT_MAX ? -1 : ans;
    }
};

