class Solution {
public:

    double helper(vector<int>& dist, int speed)
    {
        double time = 0.0;
        int len = dist.size();

        for(int i = 0; i < len-1; i++)
        {
            double t = (double)dist[i] / (double)speed;
            time += ceil(t);
        }
        time += (double)dist[len-1] / (double)speed;

        return time;
    }


    int minSpeedOnTime(vector<int>& dist, double hour) {
        int l = 1;
        int r = 1e7;

        int speed = -1;

        while(l <= r)
        {
            int mid = l+(r-l)/2;
            if(helper(dist,mid) <= hour)
            {
                speed = mid;
                r = mid - 1;
            }
            else
            {
                l = mid + 1;
            }
        }
        return speed;

    }
};
```

