class Solution {
public:
    double timeReq(vector<int>&d, int ele){
       int n=d.size();
       double t=0.0;

       for(int i=0;i<n;i++){

           double time=(double)d[i]/(double)ele;

           // we have to round off to next integer, if its not the last ride
           t+=(i==n-1?time:ceil(time));
       }

    return t;

    }
    int minSpeedOnTime(vector<int>& dist, double hour) {

        int n=dist.size();
        int res=-1;

        int l=1;
        int r=1e9+7;

        while(l<=r){
            int mid=l+(r-l)/2;

            if(timeReq(dist,mid)<=hour){
                res=mid;
                r=mid-1;
            }
            else{
                l=mid+1;
            }

        }

    return res;

    }
};
```

