class Solution {
public:
    int minSpeedOnTime(vector<int>& dist, double hour) {
        int n = dist.size();
        if(hour <= 1.0*(n-1)) return -1;

        int low = 1;
        int high = 1e7;
        while(low <= high) {
            int mid = low + (high-low)/2;

            double x = isPossible(dist,mid);
            if(x <= hour) high = mid-1;
            else low = mid+1;
        }
        return low;
    }

    double isPossible(vector<int> &dist, int mid) {
        int n = dist.size();
        double x = 0.0;
        for(int i=0 ; i<n-1 ; i++) {
            int z = (dist[i]+mid-1)/mid; // finding the ceil of dist[i]/mid
            x += 1.0*z;
        }
        x += (double)(1.0*dist[n-1])/(mid*1.0);
        return x;
    }
};
```

