class Solution {
public:
    bool feasible(int speed, vector<int>& nums, double hour){
        double time=0;
        int n=nums.size();
        for(int i=0; i<n; i++){
            if(i==n-1){
                time+=(double)nums[i]/(double)speed;
            }
            else{
                double t;
                if(nums[i]%speed==0) t=nums[i]/speed;
                else t=1+(nums[i]/speed);
                time+=t;
            }
        }

        return (time<=hour);
    }

    int minSpeedOnTime(vector<int>& dist, double hour) {
        int ans=-1;
        int low=1;
        int high=1e8;
        while(low<=high){
            int mid = low + (high-low)/2;
            if(feasible(mid, dist, hour)){
                ans=mid;
                high=mid-1;
            }
            else{
                low=mid+1;
            }
        }
        return ans;
    }
};
```

