class Solution {
public:
    int minSpeedOnTime(vector<int>& dist, double hour) {

        // length of input
        int n = dist.size();

        // range of binary search
        int lo = 1 , hi = 1e7;
        int minSpeed = -1;

        // binary search
        while(lo <= hi){

            // find mid or you can say current speed
            int mid = lo + (hi - lo) / 2;
            double currTime = 0;

            for(int i=0;i<n-1;i++){

                // if any distance is not completely divisible current speed 
                // then add one otherwise do nothing
                if(dist[i] % mid) currTime += (dist[i] / mid) + 1;
                else currTime += (dist[i] / mid);

            }

            // last element need exact division
            currTime += ((double)dist[n-1] / (double)mid);

            // check for minimum speed and go for a valid answer
            if(currTime <= hour){
                minSpeed = mid;
                hi = mid - 1;
            }
            else{
                lo = mid + 1;
            }

        }

        // return minimum speed
        return minSpeed;
    }
};
```

