public class Solution {
    public int MinSpeedOnTime(int[] dist, double hour) {
        var n = dist.Length;
        var res = Int32.MaxValue;

        var left = 1;
        var right = 10000000;

        while (left <= right) {
            var speed = left + (right - left) / 2;

            double totalTime = 0;
            for (var i = 0; i < n - 1; i++) {
                var d = dist[i];
                totalTime += (d / speed) + (d % speed > 0 ? 1 : 0);
            }

            totalTime += ((double)dist[n - 1]) / ((double)speed);

            if (totalTime - hour > 0) {
                left = speed + 1;
            } else {
                res = Math.Min(res, speed);
                right = speed - 1;
            }
        }

        return res == Int32.MaxValue ? -1 : res;
    }
}
```

