public void merge(int[] nums1, int m, int[] nums2, int n) {
    int maxIndex = nums1.length - 1;
    while (m > 0 || n > 0) {
        if (n == 0) {
            nums1[maxIndex--] = nums1[--m];
            continue;
        }
        if (m == 0) {
            nums1[maxIndex--] = nums2[--n];
            continue;
        }
        nums1[maxIndex--] = (nums1[m - 1] < nums2[n - 1]) ? nums2[--n] : nums1[--m];
    }
}

class Solution {
public:
    void merge(vector<int>& nums1, int m, vector<int>& nums2, int n) {
        int i = m - 1;
    int j = n - 1;
    int k = (m + n - 1);

    while (i >= 0 && j >= 0)
    {

        if (nums1[i] > nums2[j])
        {
            nums1[k--] = nums1[i--];
        }

        else
        {
            nums1[k--] = nums2[j--];
        }
    }

    while (i >= 0)
    {

        nums1[k--] = nums1[i--];
    }

    while (j >= 0)
    {

        nums1[k--] = nums2[j--];
    }
    }
};

