    def maxProfit(self, k, prices):
        def recursive(i, k):
            if i <= 0 or k <= 0:
                return 0
            if (i, k) in mp:
                return mp[(i, k)]
            max_profit = recursive(i - 1, k)
            for j in range(i):
                tmp = prices[i] - prices[j] + recursive(j - 1, k - 1)
                max_profit = max(max_profit, tmp)
            mp[(i, k)] = max_profit
            return mp[(i, k)]

        mp = {}
        return recursive(len(prices) - 1, k)
